import React from "react";
import { Route, Switch, HashRouter, Redirect } from 'react-router-dom';
import Login from "../views/login";
import CadastroUsuario from "../views/cadastroUsuario";
import Home from '../views/home'
import ConsultaLancamentos from "../views/lancamentos/consulta-lancamentos";
import CadastroLancamentos from "../views/lancamentos/cadastro-lancamentos";
import { AuthConsumer } from "./provedorAutenticacao";



function RotaAutenticada( { component: Component, isUsuarioAutenticado, ...props}  ){
    return(
      <Route {...props} render={ (componentProps) => {
              if(isUsuarioAutenticado){
                return(
                    <Component {...componentProps} />
                )
              }else{
                return(
                  <Redirect to={ {pathname : '/login', state: { from: componentProps.location}  } }/>
                  //de onde foi feito o acesso para Redirect para o logim
                )
              }       
          }}  />
    )
}



function Rotas(props) {
  return (
    <HashRouter>
      <Switch>
        <Route path="/login" component={Login} />
        <Route path="/cadastro-usuarios" component={CadastroUsuario} />

        <RotaAutenticada isUsuarioAutenticado={props.isUsuarioAutenticado} path="/home" component={Home} />
        <RotaAutenticada isUsuarioAutenticado={props.isUsuarioAutenticado} path="/consulta-lancamentos" component={ConsultaLancamentos} />
        <RotaAutenticada isUsuarioAutenticado={props.isUsuarioAutenticado} path="/cadastro-lancamentos/:id?" component={CadastroLancamentos} />
        {/* /cadastro-lancamentos/:id - posso passar paraêmtros com a rora*/}
        {/* Outras rotas aqui, se você tiver */}
      </Switch>
    </HashRouter>
  );
}

//função nao tem contextType
export default () =>(
    <AuthConsumer>
      { (context) =>( 
      <Rotas isUsuarioAutenticado={context.isAutenticado} /> 
      )}
    </AuthConsumer>
)
