import React from "react";
import Card from "../../components/card";
import FormGroup from "../../components/form-group";

import { withRouter } from "react-router-dom";

import LancamentoService from "../../app/service/lancamentoService";
import selectMenu from "../../components/selectMenu";
import SelectMenu from "../../components/selectMenu";

class CadastroLancamentos extends React.Component{

    state = {
        id: null,
        descricao: '',
        valor: '',
        mes: '',
        ano: '',
        tipo: '',
        status: '',
        usuario: null,
        atualizando: false
    }

    constructor(){
        super();
        this.service = new LancamentoService();
    }

    handleChange = (event) => {
        const value = event.target.value;
        const name = event.target.name;

        this.setState({ [name] : value })
    }


    render(){

       const  tipos = this.service.obterListaTipos();
       const  meses = this.service.obterListaMeses();

        return(
            <Card>
                <div className="row">
                    <div className="col-md-12">
                        <FormGroup id="inputDescricao" label="Descricao" >
                            <input id="inputDescricao" 
                                   type="text" 
                                   className="form-control"
                                   name="descricao"
                                   value={this.value.desccricao}
                                   onChange={this.handleChange}></input>
                        </FormGroup>
                    </div>
                    <div className="col-md-6">
                        <FormGroup id="inputAno" label="Ano:" >
                            <input id="inputAno" type="text" className="form-control"></input>
                        </FormGroup>
                    </div>
                    <div className="col-md-6">
                        <FormGroup id="inputMes" label="Mês:" >
                            <SelectMenu input="inputMes"  lista={meses} className="form-control"/>
                        </FormGroup>
                    </div>
                </div>

                <div className="row">
                    <div className="col-md-4">
                        <FormGroup id="inputValor" label="Valor" >
                            <input id="inputValor" type="text" className="form-control"></input>
                        </FormGroup>
                    </div>
                    <div className="col-md-4">
                        <FormGroup id="inputTipo" label="Tipo:" >
                            <SelectMenu input="inputTipo"  lista={tipos} className="form-control"/>
                        </FormGroup>
                    </div>
                    <div className="col-md-4">
                        <FormGroup id="inputStatus" label="Status:" >
                            <input id="inputStatus" type="text" className="form-control" disabled={true}></input>
                        </FormGroup>
                    </div>
                </div>
                <div className="row">
                    <div className="col-md-6">
                        <button onClick={this.buscar} type="button" className="btn btn-success">Salvar</button>
                        <button type="button" className="btn btn-danger">Cadastrar</button>
                    </div>
                </div>
            </Card>
        )
    }


}
export default withRouter(CadastroLancamentos)
